import { getWordsByCategory } from "random-word-slugs/dist/words";
import { useState } from "react";

function Search ({getWordDefinition, getWordSynonym}) {
    const [searchValue, setSearchValue] = useState("") 
    const [searchSwitcher, setSearchSwitcher] = useState(true)
    // const buttonStyle

    function handleChange(e) {
        e.preventDefault()
        setSearchValue(e.target.value)
    }

    function handleSubmit(e) {
        e.preventDefault()
        getWordDefinition(searchValue)
    }

    const setSwitchTrue = () => setSearchSwitcher(switcher => switcher = true)
    const setSwitchFalse = () => setSearchSwitcher(switcher => switcher = false)


    return(
        <>
            <form onSubmit={handleSubmit}>
                <input type="text" name="search" value={searchValue} onChange={handleChange} />
                <button type="submit" value="Submit">Search</button>        
            </form>
            <p style={{backgroudColor: "green"}} onClick={setSwitchTrue}>Dictionary</p>
            <p onClick={setSwitchFalse}>Thesaurus</p>
        </>
    )
}

export default Search
