import { getWordsByCategory } from "random-word-slugs/dist/words";
import { useState } from "react";

function Search ({getWordDefinition, getWordSynonym}) {
    const [searchValue, setSearchValue] = useState("") 
    const [searchSwitcher, setSearchSwitcher] = useState(true)

    function handleChange(e) {
        e.preventDefault()
        setSearchValue(e.target.value)
    }

    function handleSubmit(e) {
        e.preventDefault()
        getWordDefinition(searchValue)
    }

    return(
        <>
            <form onSubmit={handleSubmit}>
                <input type="text" name="search" value={searchValue} onChange={handleChange} />
                <button type="submit" value="Submit">Search</button>        
            </form>
            <button onClick={setSearchSwitcher(switcher => switcher = true)}>Dictionary</button>
            <button onClick={setSearchSwitcher(switcher => switcher = false)}>Thesaurus</button>
        </>
    )
}

export default Search
