import styled from 'styled-components'

const Card = styled.div `
text-align: center;
background-color: #49b867;
padding: 20px;

margin: 80px 100px;
box-shadow: 0 0 20px rgba(0, 0, 0, .5), 0 0 40px rgba(0, 0, 0, 0.3);
border-radius: 5px;
`

export default Card
