import styled from 'styled-components'
import { NavLink } from 'react-router-dom'
import { useState } from 'react'


function NavBar({userLogin, loggedInUser, setLoggedInUser}) {
    const [credentials, setCredentials] = useState({
        username: '',
        password: ''
    })

    function handleChange(e){
        setCredentials({
            ...credentials, 
            [e.target.name]: e.target.value})

    }

    const loginForm = 
        <form onSubmit={(e)=>userLogin(e, credentials)}>
            <input type="text" name="username" placeholder="Name" value={credentials.username} onChange={handleChange} />
            <input type="text" name="password" placeholder="Password" value={credentials.password} onChange={handleChange}/>
            <button type="submit" name="Submit">Login</button>
        </form>
    console.log(loggedInUser)
    const loggedIn = 
        <div>
            <h3>Hi {loggedInUser.username}, you're logged in</h3>
            <button onClick={()=>setLoggedInUser(false)}>Log Out</button>
        </div>

    return(
        <Header>
            {loggedInUser ? loggedIn : loginForm}
            <NavLink to="/newuser">Create New User</NavLink>
            <br/>
        </Header>
    )
}

export default NavBar

const Header = styled.div `
    display: flex;
    margin: 10px 0;
    padding: 5px;
    background-color: #49b867;
`
