import { useEffect } from "react"
import Card from "../styled/card"

function FavoriteList({handleDeleteFavorite, favList, grabFavorites, isLoggedIn, loggedInUser}) {


    useEffect(()=>{
        if(isLoggedIn === true){ 
        grabFavorites()} 
    }, [isLoggedIn]) 




    let listOfFavorites = 
    favList.map(fav=>
        <>
            <li key={fav.id}>
                {fav.name}
                <button onClick={() => handleDeleteFavorite(fav.id)}>‚ùå</button>
            </li>
        </>
    )


    if (isLoggedIn === false){
        listOfFavorites = [null]
        }


    return(
        <Card>
            <h2>{isLoggedIn ? `${loggedInUser.username}'s Favorite Words` : 'Favorite Words'}</h2>
            {favList ? null : {listOfFavorites} }
        </Card>
    )
}

export default FavoriteList
