    let listOfFavorites =
        favoriteWords.map(word => {
        return <li key={word.id}>
                    {word.name}
                    <button onClick={() => handleDeleteFavorite(word)}>‚ùå</button>
                </li>
        })

    return(
        <Card>
            <h2>{loggedInUser ? `${loggedInUser.username}'s Favorite Words` : 'Favorite Words'}</h2>
            {listOfFavorites}
        </Card>
    )
}

